import React from 'react';
import Board from './Board';
import Chess from '../class/Chess';
import Node from '../class/Node';

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            knight: [0,0],
            destination: [7,7]
        }
    }

    startGame = () => {
        const knight = this.getRandomPosition(); 
        const destination = this.getRandomPosition();
        this.setState({
            knight,
            destination
        });
    }

    getRandomPosition = () => {
        return [this.getRandomInt(), this.getRandomInt()];
    }

    getRandomInt = () => {
        const max = 8;
        return Math.floor(Math.random() * Math.floor(max));
    }

    move = (x,y) => {
        console.log(x,y);
        this.setState({

            knight:[x, y]
        })
    }

    help = () => {
        //find steps to destination and return all steps
        const { knight, destination} = this.state;
        console.log('=====', knight);
        const chess = new Chess();
        const knightNode = new Node(knight[0], knight[1]);
        const destNode = new Node(destination[0], destination[1]);
        const steps = chess.calcSteps(knightNode, destNode);
        console.log(steps);
        //TODO steps is an array, e.g [{0,1}, {1,2}]
        // map out all elements in steps in board
        steps.forEach(step => {
            console.log(step);

            setTimeout(function() {
                console.log('inside timeout');
                this.move(step.x, step.y);
            }, 1000).bind(this);
        })

    }

    render() {
        const { knight, destination} = this.state;
        return (
            <>
                <Board 
                    knight={knight}
                    destination={destination} 
                    move={(x,y) => this.move(x,y)}/>
                <section>
                    <button 
                        className="primary"
                        onClick={this.startGame}>
                        start game
                    </button>
                    <button className="secondary"
                        onClick={this.help}>
                        help
                    </button>
                </section>
            </>
        )
    }
}



export default Game;
