import React from 'react';
import Board from './Board';

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            knight: [0,0],
            destination: [7,7]
        }
    }

    startGame = () => {
        const knightPosition = this.getRandomPosition(); 
        const destinationPosition = this.getRandomPosition();
        this.setState({
            knight: knightPosition,
            destination: destinationPosition
        });
    }

    getRandomPosition = () => {
        return [this.getRandomInt(), this.getRandomInt()];
    }

    getRandomInt = () => {
        const max = 8;
        return Math.floor(Math.random() * Math.floor(max));
      }

    render() {
        const { knight, destination} = this.state;
        return (
            <>
                <Board 
                    knight={knight}
                    destination={destination}/>           
                <section>
                <button 
                    className="primary"
                    onClick={this.startGame}>
                    start game
                </button>
                <button className="secondary">
                    help
                </button>
                </section>
            </>
        )
    }
}



export default Game;
