import React from 'react';
import { KnightWhite,  Destination, Knight } from './Icon';
import './Board.scss';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          boardSize: 8,
          boardElements: []
        }
    }

    componentDidMount() {
      this.init();
    }

    init = () => {
      this.setState({
        boardElements: [0,1,2,3,4,5,6,7]
      })
    }

    isKnight = (x, y) => {
      console.log(this.props.knight);
      const { knight } = this.props;
      return x === knight[0] && y === knight[1] && x%2 === 0 && y%2 === 1;
    }

    isDestination = (x, y) => {
      const { destination } = this.props;
      return x === destination[0] && y === destination[1];
    }

    isKnightWhite = (x, y) => {
      const { knight} = this.props;
      return x === knight[0] && y === knight[1] && x%2 === 1 && y%2 === 0;
    }


    render() {
        const { boardElements } = this.state;
        const { knight, destination } = this.props;
        console.log(knight, destination);
        return (
          <ul>
            {boardElements.map(x => 
              <li key={x}>
                <ul>
                  {boardElements.map(y => 
                    <li key={`${x}-${y}`}>
                      {this.isKnight(x, y) && <Knight />}
                      {this.isKnightWhite(x, y) && <Knight />}
                      {this.isDestination(x, y) && <Destination />}
                    </li>
                  )}
                </ul>
              </li>
            )}
          </ul>
        ) 
    }
}

export default Board;
