import React from 'react';
import Board from './Board';

class Game extends React.Component {
    constructor(props) {
        super(props);
    }

    startGame = () => {
        const knightPosition = this.getRandomPosition(); 
        const destinationPosition = this.getRandomPosition(); 
        console.log(knightPosition,destinationPosition );

    }

    getRandomPosition = () => {
        return [this.getRandomInt(), this.getRandomInt()];
    }

    getRandomInt = () => {
        const max = 8;
        return Math.floor(Math.random() * Math.floor(max));
      }

    render() {
        return (
            <>
                <Board />           
                <section>
                <button 
                    className="primary"
                    onClick={this.startGame}>
                    start game
                </button>
                <button className="secondary">
                    help
                </button>
                </section>
            </>
        )
    }
}



export default Game;
